const EMAIL_REGEXP=/^(([^<>()[\].,;:\s@"]+(\.[^<>()[\].,;:\s@"]+)*)|(".+"))@(([^<>()[\].,;:\s@"]+\.)+[^<>()[\].,;:\s@"]{2,})$/iu,INPUT=document.querySelector("#email");let btn=document.querySelector("#submit");function validate(e){return EMAIL_REGEXP.test(e)}function updateInput(){if(validate(INPUT.value))return INPUT.style.borderColor="green",void console.log(INPUT.value);INPUT.style.borderColor="red"}btn.addEventListener("click",updateInput);